mapper.select:
SELECT COUNT(*)
FROM ITEM
WHERE category_no = #{cate_no}


	SELECT *
	FROM (
		SELECT ROW_NUMBER() OVER (ORDER BY readhit DESC) AS NO, i.*, img.img_name
    	FROM ITEM i JOIN ITEM_IMAGEFILE img
					  ON i.item_no = img.item_no
    	WHERE category_no = #{cate_no}
	)
	WHERE NO BETWEEN #{start} AND #{end}

	SELECT *
	FROM (
		SELECT ROW_NUMBER() OVER (ORDER BY item_price ASC) AS NO, i.*, img.img_name
    	FROM ITEM i JOIN ITEM_IMAGEFILE img
					  ON i.item_no = img.item_no
    	WHERE category_no = #{cate_no}
	)
	WHERE NO BETWEEN #{start} AND #{end}

	SELECT *
	FROM (
		SELECT ROW_NUMBER() OVER (ORDER BY item_price DESC) AS NO, i.*, img.img_name
    	FROM ITEM i JOIN ITEM_IMAGEFILE img
					  ON i.item_no = img.item_no
    	WHERE category_no = #{cate_no}
	)
	WHERE NO BETWEEN #{start} AND #{end}
